INSERT INTO `operate_log` (`id`, `operate_user`, `operate_username`, `operate_time`, `class_name`, `method_name`, `method_params`, `return_value`, `cost_time`) VALUES (59, 1, 'admin', '2025-08-28 16:59:31', 'com.jsdoctor.service.impl.OriginArticleServiceImpl', 'getOriginArticleList', '[1, 20, null, null, null]', '{\"total\":426,\"pageSize\":20,\"page\":1,\"articles\":[{\"content\":\"精子可以吃，但是不建议吃。男性精子的主要成分是蛋白质，以及有锌、硒等微量元素，但是精液中含有的这些蛋白质及微量元素含量太少，对于人体吸收来说，精子中的蛋白质及微量元素微不足道，而且也并不容易吸收，并不能对人体健康有多大帮助，口服男性精子也不能起到美容养颜，延缓衰老的作用。最为关键的若男性本身带有病菌，很可能在食用精子之后造成传染，比如常见的梅毒，性病，艾滋等，这些精子对人体健康都是不起作用的，所以并不建议食用男性精子。若精子进入口腔后要及时清理口腔，避免引起口腔细菌滋生。\",\"createdAt\":\"2025-08-28T16:59:31\",\"departmentId\":\"0404\",\"departmentName\":\"泌尿外科\",\"doctor\":\"高宛生\",\"id\":1218,\"importedBy\":1,\"importedByUsername\":\"admin\",\"parentDepartmentName\":\"外科\",\"position\":\"副主任医师\",\"searchTitle\":\"男人的精子真的能吃吗\",\"status\":1,\"title\":\"精子可以吃吗\",\"updatedAt\":\"2025-08-28T16:59:31\"},{\"content\":\"天气冷会影响到男人的性功能，造成阴茎的勃起障碍。天气冷会导致阴茎的海绵体血液循环减少、血管收缩，阴茎出现短缩或者勃起时充血不足。可以通过用手摩擦或者到温暖的环境里，即可恢复正常。如果是性功能障碍导致无法勃起，主要是因为肾虚或者生殖系统疾病导致的，也可能是精神紧张、身体疲劳。\\n\\n天冷时需要控制性生活的频率，因为性生活会消耗人体体能，造成人体热量流失。在天冷时要适当的锻炼，做有氧运动，增强体质，同时需要放松心情，戒除不良生活习惯包括抽烟，喝酒等等。可以服用一些补肾、壮阳的食材和药材。出现了勃起功能障碍，一定要到正规医院进行详细的检查，明确具体的病因，然后进行针对性的治疗。\",\"createdAt\":\"2025-08-28T16:59:31\",\"departmentId\":\"0404\",\"departmentName\":\"泌尿外科\",\"doctor\":\"曲华伟\",\"id\":1206,\"impor...', 18);
