-- ----------------------------
-- Table structure for articles
-- ----------------------------
DROP TABLE IF EXISTS `articles`;
CREATE TABLE `articles` (
  `id` int NOT NULL AUTO_INCREMENT,
  `title` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '文章标题',
  `content` text COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '文章内容（纯文本）',
  `author_id` int NOT NULL COMMENT '作者ID',
  `origin_article_id` int DEFAULT NULL COMMENT '关联的原始文章ID',
  `assigned_doctor_id` int DEFAULT NULL COMMENT '分派的医生ID',
  `status` int DEFAULT '1' COMMENT '文章状态：1-导入中 2-未回答 3-待审核 4-待修改 5-审核通过 6-作废 7-编写换题',
  `publish_status` int DEFAULT '0' COMMENT '发布状态：0-未发布 1-已发布',
  `published_at` timestamp NULL DEFAULT NULL COMMENT '发布时间',
  `publish_platform` varchar(50) COLLATE utf8mb4_unicode_ci DEFAULT 'baijiahao' COMMENT '发布平台',
  `submitted_at` timestamp NULL DEFAULT NULL COMMENT '提交时间',
  `reviewed_at` timestamp NULL DEFAULT NULL COMMENT '审核时间',
  `reviewed_by` int DEFAULT NULL COMMENT '审核员ID',
  `review_comment` text COLLATE utf8mb4_unicode_ci COMMENT '审核意见',
  `created_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  KEY `idx_author_id` (`author_id`),
  KEY `idx_status` (`status`),
  KEY `idx_submitted_at` (`submitted_at`),
  KEY `idx_origin_article_id` (`origin_article_id`),
  KEY `idx_assigned_doctor_id` (`assigned_doctor_id`),
  KEY `idx_articles_publish_status` (`publish_status`)
) ENGINE=InnoDB AUTO_INCREMENT=121 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='文章表（包含发布状态管理）';

